javascript:(function(){const contextMenu=document.createElement('div');contextMenu.id='chatgpt-context-menu';contextMenu.style.position='absolute';contextMenu.style.color='#000';contextMenu.style.background='#fff';contextMenu.style.border='1px solid #ccc';contextMenu.style.padding='8px 12px 12px 12px';contextMenu.style.borderRadius='5px';contextMenu.style.boxShadow='0 2px 8px rgba(0,0,0,0.2)';contextMenu.style.cursor='pointer';contextMenu.style.display='none';contextMenu.style.zIndex=99999;contextMenu.style.transition='opacity 0.3s, transform 0.3s';contextMenu.style.opacity=0;contextMenu.style.transform='translateY(20px)';contextMenu.style.minWidth='120px';contextMenu.style.fontFamily='Arial,sans-serif';contextMenu.style.direction='rtl';const closeButton=document.createElement('span');closeButton.innerHTML='✕';closeButton.style.position='absolute';closeButton.style.top='2px';closeButton.style.right='6px';closeButton.style.cursor='pointer';closeButton.style.fontSize='12px';closeButton.style.color='#666';closeButton.addEventListener('click',()=>{hideContextMenu();});contextMenu.appendChild(closeButton);const menuLabel=document.createElement('div');menuLabel.innerText='שאל את ChatGPT';menuLabel.style.marginTop='10px';contextMenu.appendChild(menuLabel);document.body.appendChild(contextMenu);let selectedText='';function showContextMenu(x,y){contextMenu.style.top=y+'px';contextMenu.style.left=x+'px';contextMenu.style.display='block';setTimeout(()=>{contextMenu.style.opacity=1;contextMenu.style.transform='translateY(0)';},10);}function hideContextMenu(){contextMenu.style.opacity=0;contextMenu.style.transform='translateY(20px)';setTimeout(()=>contextMenu.style.display='none',300);}document.addEventListener('keydown',event=>{if(event.shiftKey&&event.key.toLowerCase()==='q'){selectedText=window.getSelection().toString().trim();if(selectedText){const rangeRect=window.getSelection().getRangeAt(0).getBoundingClientRect();showContextMenu(rangeRect.right+window.scrollX,rangeRect.bottom+window.scrollY);}}});document.addEventListener('click',event=>{if(!contextMenu.contains(event.target)){hideContextMenu();}});menuLabel.addEventListener('click',()=>{if(selectedText){window.open('https://chat.openai.com/?q='+encodeURIComponent(selectedText),'_blank','width=600,height=400');}hideContextMenu();});})();
